mapper.select:
SELECT

FROM t_wx_user_meeting a

WHERE a.id = #{id} and flag=#{flag}

SELECT
	count(*) as count
FROM t_wx_user_meeting a



		AND a.user_name = #{userName}


		AND a.flag = #{flag}


		AND a.phone = #{phone}

	AND flag=#{flag}

		SELECT

		FROM
		(SELECT id from  t_wx_user_meeting t



				AND t.user_name = #{userName}


				AND t.flag = #{flag}


				AND t.phone = #{phone}

			AND flag=#{flag}

		 limit #{startIndex},#{pageSize}) b inner join
		t_wx_user_meeting a  on b.id=a.id


                 ORDER BY update_time desc

		SELECT

		FROM (SELECT id from  t_wx_user_meeting t


				AND t.user_name = #{userName}


				AND t.flag = #{flag}


				AND t.phone = #{phone}

			AND flag=#{flag}

		) b  inner join t_wx_user_meeting a  on b.id=a.id


                 ORDER BY update_time desc

SELECT

FROM
t_wx_user_meeting a


		AND a.user_name = #{userName}


		AND a.flag = #{flag}


		AND a.phone = #{phone}

	AND flag=#{flag}

limit 1

mapper.update:
UPDATE t_wx_user_meeting SET
flag = -1
	WHERE id = #{id}
