mapper.select:
SELECT

FROM t_sys_dict a

WHERE a.id = #{id}

SELECT
count(0) as count
FROM t_sys_dict a


    a.flag = #{flag}

        AND a.DICT_KEY = #{dictKey}


        AND a.FLAG = #{flag}


        AND a.TYPE = #{type}

SELECT

FROM t_sys_dict a inner join
(SELECT id from t_sys_dict a

    a.flag = #{flag}

        AND a.DICT_KEY = #{dictKey}


        AND a.FLAG = #{flag}


        AND a.TYPE = #{type}




        ORDER BY a.UPDATE_TIME desc


limit #{startIndex},#{pageSize}
) b on b.id=a.id

SELECT

FROM t_sys_dict a


    a.flag = #{flag}

        AND a.DICT_KEY = #{dictKey}


        AND a.FLAG = #{flag}


        AND a.TYPE = #{type}




        ORDER BY a.UPDATE_TIME desc
